$.fn.extend({bannPlayer:function(options){options=$.extend({hover:"current",speed:300,interval:500,changeWay:"up",isAuto:true,arrUrl:null},options);var picBox=$(this).children().eq(0),btns=$(this).children().eq(1).children(),changeType="",len=btns.length,index=0,prevIndex=0,clearT=null,timeoutId=null;if(len==1){return false;}
btns.eq(0).addClass(options.hover);if(options.switchBtn){var switchBtn=$(options.switchBtn);if(options.isAuto){switchBtn.get(0).innerHTML="‖";switchBtn.click(function(){if(switchBtn.get(0).innerHTML=="‖"){switchBtn.get(0).innerHTML="▶";clearTimeout(timeoutId);options.isAuto=false;}else{switchBtn.get(0).innerHTML="‖";options.isAuto=true;}});}else{switchBtn.get(0).innerHTML="▶";switchBtn.click(function(){if(switchBtn.get(0).innerHTML=="▶"){switchBtn.get(0).innerHTML="‖";options.isAuto=true;}else{switchBtn.get(0).innerHTML="▶";clearTimeout(timeoutId);options.isAuto=false;}});}}
var exchange=function(obj1,obj2){var temp=null;temp=obj1;obj1=obj2;obj2=temp;picLi=obj1,picLiSib=obj2;};var picHide=function(){picLi.stop(true,true).fadeOut(options.speed);}
var picReplace=function(index){picLiSib.find("img").attr("src",options.arrUrl[index]);picLiSib.children().attr("href",options.arrUrl[index]);picLiSib.stop(true,true).fadeIn(options.speed);exchange(picLi,picLiSib);};var loading=function(index){picHide();var img=document.createElement("img");$(img).load(function(){loadStatus[index]=true;picReplace(index);}).attr("src",options.arrUrl[index]);};var preloading=function(index){if(loadStatus[index]){if(loadStatus.length==options.arrUrl.length){preloading=function(index){picHide();picReplace(index);}
preloading(index);}else{picHide();picReplace(index);}}else{loading(index);}};if(options.changeWay=="up"||options.changeWay=="left"){var rollWay="",rollWidth,style={};changeType="rollmode";rollWay=options.changeWay=="left"?"left":"top";rollWidth=options.changeWay=="left"?$(this).width():$(this).height();}else if(options.changeWay=="fade"){var pics=picBox.children(),currentIndex=-1,oldIndex=len-1;pics.eq(oldIndex).siblings().hide();changeType="fademode";}else if(options.changeWay=="lazyLoad"){var picLi=picBox.children().eq(index),picLiSib=picLi.siblings(),loadStatus=[];loading(index);changeType="lazymode";}
var change=function(index){if(changeType=="fademode"){change=function(index){currentIndex=len-index-1;pics.eq(oldIndex).stop(true,true).fadeOut(options.speed);pics.eq(currentIndex).stop(true,true).fadeIn(options.speed);btnsChange(index);oldIndex=currentIndex;}}else if(changeType=="rollmode"){change=function(index){style[rollWay]=-(rollWidth*index);picBox.stop(true,true).animate(style,options.speed);btnsChange(index);}}else if(changeType=="lazymode"){change=function(index){preloading(index);btnsChange(index);}}
return change(index);};var btnsChange=function(index){btns.eq(index).addClass(options.hover).siblings().removeClass(options.hover);prevIndex=index;};var autoPlay=function(){timeoutId=setTimeout(function(){index++;if(index==len){index=0;};change(index);timeoutId=setTimeout(arguments.callee,options.interval);},options.interval);};if(options.isAuto){autoPlay();}
if(options.prevBtn&&options.nextBtn){var pairBtns=true;$(options.prevBtn).click(function(){if(index==0){index=len-1;}else{index-=1;}
change(index);});$(options.nextBtn).click(function(){if(index==len-1){index=0;}else{index+=1;}
change(index);});}
btns.mouseenter(function(){index=btns.index(this);if(index!=prevIndex){clearTimeout(clearT);clearT=setTimeout(function(){change(index);},150);}});$(this).mouseleave(function(){if(pairBtns){$(options.prevBtn).stop(true,true).fadeOut();$(options.nextBtn).stop(true,true).fadeOut();}
if(options.isAuto){autoPlay();}});$(this).mouseenter(function(){if(pairBtns){$(options.prevBtn).stop(true,true).fadeTo("slow",0.5);$(options.nextBtn).stop(true,true).fadeTo("slow",0.5);}
if(options.isAuto){clearTimeout(timeoutId);}});}});